}

.dark {
  --background: hsl(220, 13%, 9%);
  --foreground: hsl(210, 40%, 98%);
  --muted: hsl(220, 13%, 14%);
  --muted-foreground: hsl(220, 6%, 65%);
  --popover: hsl(220, 13%, 9%);
