}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    background: linear-gradient(135deg, hsl(261, 73%, 97%) 0%, hsl(0, 0%, 100%) 50%, hsl(140, 20%, 97%) 100%);
    min-height: 100vh;
  }
}

@layer components {
  .wellness-card {
    @apply bg-white rounded-2xl shadow-lg border border-gray-100 p-6 hover:shadow-xl transition-shadow duration-300;
  }

  .score-circle {
    @apply relative w-24 h-24 mx-auto;
  }

  .btn-primary {
    @apply bg-primary text-primary-foreground hover:opacity-90 px-6 py-3 rounded-xl font-medium transition-colors duration-200;
  }

  .btn-secondary {
    @apply bg-secondary text-secondary-foreground hover:opacity-90 px-6 py-3 rounded-xl font-medium transition-colors duration-200;
  }

  .btn-coral {
    @apply bg-coral text-coral-foreground hover:opacity-90 px-6 py-3 rounded-xl font-medium transition-colors duration-200;
  }

  .gradient-primary {
    background: linear-gradient(135deg, hsl(261, 73%, 66%) 0%, hsl(261, 73%, 56%) 100%);
  }

  .gradient-sage {
    background: linear-gradient(135deg, hsl(140, 20%, 65%) 0%, hsl(140, 20%, 55%) 100%);
  }

  .gradient-coral {
    background: linear-gradient(135deg, hsl(14, 86%, 76%) 0%, hsl(14, 86%, 66%) 100%);
  }
}

