            <p className="text-muted-foreground">Preparing your wellness journey...</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Show individual component
  if (activeComponent && activeModuleId) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-rose-50 to-teal-50 p-4">
